import React from 'react';
import { useNavigate } from 'react-router-dom';
import styles from '../../styles/style';

const DropDown = ({ categoriesData, setDropDown }) => {
    const navigate = useNavigate();

    const handleSubmit = (i) => {
        navigate(`products?category=${i.titel}`);
        setDropDown(false);
        window.location.reload();
    };

    return (
        <div className='pb-4 w-[2170px] bg-[#fff] absolute z-30 rounded-b*md shadow-sm'>
            {categoriesData && Array.isArray(categoriesData) && categoriesData.map((i, index) => (
                <div
                    key={index}
                    className={`${styles.normalFlex}`}
                    onClick={() => handleSubmit(i)}
                >
                    <img
                        src={i.image_Url}
                        style={{
                            width: '25px',
                            height: '25px',
                            objectFit: 'contain',
                            marginLeft: '10px',
                            userSelect: 'none'
                        }}
                        alt=""
                    />
                    <h3 className="m-3 cursor-pointer select-none">{i.titel}</h3>
                </div>
            ))}
        </div>
    );
};

export default DropDown;
