import axios from "axios";
import React, { useEffect, useState } from "react";
import { withRouter } from "react-router-dom"; // Import withRouter

import { server } from "../server";

const ActivationPage = ({ match, history }) => {
	// Destructure match and history from props
	const { activation_token } = match.params; // Get activation_token from match.params
	const [error, setError] = useState(false);

	useEffect(() => {
		if (activation_token) {
			const sendRequest = async () => {
				await axios
					.post(`${server}/user/activation`, {
						activation_token,
					})
					.then((res) => {
						console.log(res);
					})
					.catch((err) => {
						setError(true);
					});
			};
			sendRequest();
		}

		// Navigate to /login after 5 seconds (5000 milliseconds)
		const timeout = setTimeout(() => {
			history.push("/login");
		}, 5000);

		// Clear the timeout on component unmount to avoid memory leaks
		return () => clearTimeout(timeout);
	}, [activation_token, history]); // Include activation_token and history in the dependency array

	return (
		<div
			style={{
				width: "100%",
				height: "100vh",
				display: "flex",
				justifyContent: "center",
				alignItems: "center",
			}}
		>
			{error ? (
				<p>Your token is expired!</p>
			) : (
				<p>Your account has been created successfully!</p>
			)}
		</div>
	);
};

export default withRouter(ActivationPage); // Wrap the component with withRouter
