import React, { useState } from "react";
import { Link } from "react-router-dom";
import styles from "../../styles/style";
import { categoriesData, productData } from "../../static/data";
import {
  AiOutlineHeart,
  AiOutlineSearch,
  AiOutlineShoppingCart,
} from "react-icons/ai";
import { IoIosArrowForward, IoIosArrowDown } from "react-icons/io";
import { BiMenuAltLeft } from "react-icons/bi";
import { CgProfile } from "react-icons/cg";
import DropDown from "./DropDown";
import Navbar from "./Navbar";

const Header = () => {
  const [searchTerm, setSearchTerm] = useState("");
  const [searchData, setSearchData] = useState(null);
  const [active, setActive] = useState(false);
  const [isDropDownOpen, setDropDown] = useState(false);

  const handleSearchChange = (e) => {
    const term = e.target.value;
    setSearchTerm(term);

    const filteredProducts =
      productData &&
      productData.filter((product) =>
        product.name.toLowerCase().includes(term.toLowerCase())
      );
    setSearchData(filteredProducts);
  };

  window.addEventListener("scroll", () => {
    if (window.scrollY > 70) {
      setActive(true);
    } else {
      setActive(false);
    }
  });

  return (
    <>
      <div className=" flex items-center vh-100  ">
        <div>
          <Link to="/">
            <img
              className="mt-5 ml-10"
              src="https://shopo.quomodothemes.website/assets/images/logo.svg"
              alt=""
            />
          </Link>
        </div>
        <div className="relative flex items-center w-[50%] self-center ml-40">
  <input
    type="text"
    placeholder="search product..."
    value={searchTerm}
    onChange={handleSearchChange}
    className="h-[40px] w-full px-10 border-[#3975db] border-[2px] rounded-md"
  />
  <AiOutlineSearch size={30} className="absolute top-1.5 right-4 cursor-pointer" />

  {searchData && searchData.length !== 0 ? (
    <div className="absolute min-h-[30vh] bg-slate-50 shadow-sm-2 z-[9] p-4">
      {searchData &&
        searchData.map((i, index) => {
          const d = i.name;
          const Product_name = d.replace(/\s+/g, "-");
          return (
            <Link to={`/product/${Product_name}`} key={index}>
              <div className="w-full flex items-start py-3">
                <img
                  src={i.image_Url[0].url}
                  alt=""
                  className="w-[40px] h-[40px] mr-[10px]"
                />
                <h1>{i.name}</h1>
              </div>
            </Link>
          );
        })}
    </div>
  ) : null}
  <div className="flex items-center">
  <div className={`${styles.button}`} >
            <Link to="/">
              <h1 className="text-[#fff] flex items-center">
                Become Seller <IoIosArrowForward className="ml-1" />
              </h1>
            </Link>
          </div>
          </div>
  
</div>




      </div>
      <div className={`${styles.section}`}>
        <div className="hidden 800px:h-[50px] 800px:my-[20px] 800px:flex items-center vh-100 justify-between">
        
        </div>
        <div
          className={`${
            active === true ? "shadow-sm fixed top-0 left-0 z-10" : null
          } transition hidden 800px:flex itemscenter justify-between w-full bg-[#3321c8] h-[70px]`}
        >
          <div
            className={`${styles.section} relative ${styles.noramlFlex} justifi-between`}
          >
            <div>
              <div className=" relative h-[60px] mt-[10px] w-[270px] hidden 1000px:block">
                <BiMenuAltLeft size={3} className="absolute top-3 left-2" />
                <button
                  className={`h-[100%] w-full flex justify-between items-center pl-10 bg-white font-sans text-lg font-[500] select-none rounded-t-md`}
                >
                  All categories
                </button>
                <IoIosArrowDown
                  size={20}
                  className="absolute right-2 top-4 cursor-pointer"
                  onClick={() => setDropDown(!isDropDownOpen)}
                />
                {isDropDownOpen ? (
                  <DropDown
                    categoriesData={categoriesData}
                    setDropDown={setDropDown}
                  />
                ) : null}
              </div>
            </div>
            <div className={`${styles.noramlFlex}`}>
              <Navbar active={active} />
            </div>
          </div>
          <div className="flex">
            <div className={`${styles.noramlFlex}`}>
              <div className="relative cursor-pointer mr-[15px]">
                <AiOutlineHeart size={30} color="rgb(255 255 255 / 83%)" />
                <span className="absolute right-0 top-0 rounded-full bg-[#3bc177] w-4 h-4 top right p-0 m-0 text-white font-mono text-[12px] leading-tight text-center">
                  0
                </span>
              </div>
            </div>
            <div className={`${styles.noramlFlex}`}>
              <div className="relative cursor-pointer mr-[15px]">
                <AiOutlineShoppingCart
                  size={30}
                  color="rgb(255 255 255 / 83%)"
                />
                <span className="absolute right-0 top-0 rounded-full bg-[#3bc177] w-4 h-4 top right p-0 m-0 text-white font-mono text-[12px] leading-tight text-center">
                  1
                </span>
              </div>
            </div>
            <div className={`${styles.noramlFlex}`}>
              <div className="relative cursor-pointer mr-[15px]">
                <Link to="/login">
                  <CgProfile size={30} color="rgb(255 255 255 / 83%)" />
                </Link>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default Header;
