import axios from "axios";
import React, { useEffect, useState } from "react";
import { Redirect, useParams } from "react-router-dom"; // Import Redirect
import { server } from "../server";

const ActivationPage = () => {
	const { activation_token } = useParams();
	const [error, setError] = useState(false);
	const [activated, setActivated] = useState(false); // New state to track activation status

	useEffect(() => {
		if (activation_token) {
			const sendRequest = async () => {
				try {
					const res = await axios.post(`${server}/user/activation`, {
						activation_token,
					});
					console.log(res);
					setActivated(true); // Set activated to true upon successful activation
				} catch (err) {
					setError(true);
				}
			};
			sendRequest();
		}
	}, [activation_token]);

	// If activated, redirect to /login
	if (activated) {
		return <Redirect to='/login' />;
	}

	return (
		<div
			style={{
				width: "100%",
				height: "100vh",
				display: "flex",
				justifyContent: "center",
				alignItems: "center",
			}}
		>
			{error ? (
				<p>Your token is expired!</p>
			) : (
				<p>Your account has been created successfully!</p>
			)}
		</div>
	);
};

export default ActivationPage;
