import axios from "axios";
import { server } from "../../server";

// Action Types
export const ActionTypes = {
	LoadUserRequest: "LoadUserRequest",
	LoadUserSuccess: "LoadUserSuccess",
	LoadUserFail: "LoadUserFail",
};

// Action Creators
export const loadUser = () => async (dispatch) => {
	try {
		dispatch({ type: ActionTypes.LoadUserRequest });
		const { data } = await axios.get(`${server}/user/getuser`, {
			withCredentials: true,
		});
		dispatch({ type: ActionTypes.LoadUserSuccess, payload: data.user });
	} catch (error) {
		dispatch({
			type: ActionTypes.LoadUserFail,
			payload: error.response?.data?.message || "Unknown error",
		});
	}
};
