import { createReducer } from "@reduxjs/toolkit";

// Define action types as constants
const LoadUserRequest = "LoadUserRequest";
const LoadUserSuccess = "LoadUserSuccess";
const LoadUserFail = "LoadUserFail";
const updateUserInfoRequest = "updateUserInfoRequest";
const UPDATE_USER_INFO_SUCCESS = "UPDATE_USER_INFO_SUCCESS";
const UPDATE_USER_INFO_FAILED = "UPDATE_USER_INFO_FAILED";
const UPDATE_USER_ADDRESS_REQUEST = "UPDATE_USER_ADDRESS_REQUEST";
const UPDATE_USER_ADDRESS_SUCCESS = "UPDATE_USER_ADDRESS_SUCCESS";
const UPDATE_USER_ADDRESS_FAILED = "UPDATE_USER_ADDRESS_FAILED";
const DELETE_USER_ADDRESS_REQUEST = "DELETE_USER_ADDRESS_REQUEST";
const DELETE_USER_ADDRESS_SUCCESS = "DELETE_USER_ADDRESS_SUCCESS";
const DELETE_USER_ADDRESS_FAILED = "DELETE_USER_ADDRESS_FAILED";
const GET_ALL_USERS_REQUEST = "GET_ALL_USERS_REQUEST";
const GET_ALL_USERS_SUCCESS = "GET_ALL_USERS_SUCCESS";
const GET_ALL_USERS_FAILED = "GET_ALL_USERS_FAILED";
const CLEAR_ERRORS = "CLEAR_ERRORS";
const CLEAR_MESSAGES = "CLEAR_MESSAGES";

const initialState = {
	isAuthenticated: false,
	loading: false,
	addressloading: false,
	successMessage: null,
	error: null,
	user: null,
	users: [],
};

export const userReducer = createReducer(initialState, (builder) => {
	builder
		.addCase(LoadUserRequest, (state) => {
			state.loading = true;
		})
		.addCase(LoadUserSuccess, (state, action) => {
			state.isAuthenticated = true;
			state.loading = false;
			state.user = action.payload;
		})
		.addCase(LoadUserFail, (state, action) => {
			state.loading = false;
			state.error = action.payload;
			state.isAuthenticated = false;
		})
		.addCase(updateUserInfoRequest, (state) => {
			state.loading = true;
		})
		.addCase(UPDATE_USER_INFO_SUCCESS, (state, action) => {
			state.loading = false;
			state.user = action.payload;
		})
		.addCase(UPDATE_USER_INFO_FAILED, (state, action) => {
			state.loading = false;
			state.error = action.payload;
		})
		.addCase(UPDATE_USER_ADDRESS_REQUEST, (state) => {
			state.addressloading = true;
		})
		.addCase(UPDATE_USER_ADDRESS_SUCCESS, (state, action) => {
			state.addressloading = false;
			state.successMessage = action.payload.successMessage;
			state.user = action.payload.user;
		})
		.addCase(UPDATE_USER_ADDRESS_FAILED, (state, action) => {
			state.addressloading = false;
			state.error = action.payload;
		})
		.addCase(DELETE_USER_ADDRESS_REQUEST, (state) => {
			state.addressloading = true;
		})
		.addCase(DELETE_USER_ADDRESS_SUCCESS, (state, action) => {
			state.addressloading = false;
			state.successMessage = action.payload.successMessage;
			state.user = action.payload.user;
		})
		.addCase(DELETE_USER_ADDRESS_FAILED, (state, action) => {
			state.addressloading = false;
			state.error = action.payload;
		})
		.addCase(GET_ALL_USERS_REQUEST, (state) => {
			state.usersLoading = true;
		})
		.addCase(GET_ALL_USERS_SUCCESS, (state, action) => {
			state.usersLoading = false;
			state.users = action.payload;
		})
		.addCase(GET_ALL_USERS_FAILED, (state, action) => {
			state.usersLoading = false;
			state.error = action.payload;
		})
		.addCase(CLEAR_ERRORS, (state) => {
			state.error = null;
		})
		.addCase(CLEAR_MESSAGES, (state) => {
			state.successMessage = null;
		});
});
